import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Sidebar = () => (
  <div className="sidebar">
    <h2>UEcoManage</h2>
    {/* You can add your sidebar links here if needed */}
  </div>
);

const BuildingNavbar = ({ buildings = [], onSelectBuilding, activeBuilding }) => (
  <nav className="building-navbar">
    {buildings.length > 0 ? (
      buildings.map((building) => (
        <button
          key={building.id}
          className={activeBuilding === building.name ? "active" : ""}
          onClick={() => onSelectBuilding(building)}
        >
          {building.name}
        </button>
      ))
    ) : (
      <p>No buildings available</p>
    )}
  </nav>
);

const BuildingNavigation = () => {
  const [buildings, setBuildings] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [selectedBuilding, setSelectedBuilding] = useState(null);
  const [devices, setDevices] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false); 
  const [showAddRoomModal, setShowAddRoomModal] = useState(false);
  const [newRoomName, setNewRoomName] = useState('');
  const [newBuilding, setNewBuilding] = useState({ name: '', description: '' });

  const fetchBuildings = () => {
    axios.get('http://localhost:5000/api/buildings')
      .then((response) => {
        const fetchedBuildings = response.data;
        setBuildings(fetchBuildings);

        if (fetchedBuildings.length > 0) {
          setSelectedBuilding(fetchedBuildings[0]);  // Set the first building
          fetchRooms(fetchedBuildings[0]); 
        }
      })
      .catch((error) => {
        console.error('Error fetching buildings:', error);
      });
  };
  
  // Call the function inside useEffect to load buildings on mount
  useEffect(() => {
    fetchBuildings();
  }, []);

  const addBuilding = () => {
    axios.post('http://localhost:5000/api/buildings', newBuilding)
      .then(() => {
        fetchBuildings();
        setBuildings([...buildings, newBuilding]);
        setShowAddModal(false);
        setNewBuilding({ name: '', description: '' });
      })
      .catch((error) => {
        console.error('Error adding building:', error);
      });
  };

  const fetchRooms = (building) => {
    axios.get(`http://localhost:5000/api/buildings/${building.id}/rooms`)
      .then((response) => {
        setRooms(response.data);
        setSelectedBuilding(building.name);
      })
      .catch((error) => {
        console.error('Error fetching rooms:', error);
      });
  };

  const addRoom = () => {
    axios.post(`http://localhost:5000/api/buildings/${selectedBuilding.id}/rooms`, { name: newRoomName })
      .then(() => {
        fetchRooms(selectedBuilding);  // Refresh rooms after adding
        setNewRoomName('');
        setShowAddRoomModal(false);  // Close modal after adding
      })
      .catch((error) => {
        console.error('Error adding room:', error);
      });
  };

  const fetchDevices = (room) => {
    axios.get(`http://localhost:5000/api/rooms/${room.id}/devices`)
      .then((response) => {
        setDevices(response.data);
        setSelectedRoom(room);
        setShowModal(true);
      })
      .catch((error) => {
        console.error('Error fetching devices:', error);
      });
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedRoom(null);
    setDevices([]);
  };

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="main">
        <header>
          <h1>UEcoManage Admin</h1>
          <p style={{ color: '#475569', marginTop: '10px' }}>
            Monitor and manage buildings, rooms, and devices.
          </p>

            {/* add building function */}
          <div className="controls-right">
            <button onClick={setShowAddModal}>Add Building</button>
            <button>Edit Building</button>
          </div>
          
        </header>

        <BuildingNavbar
          buildings={buildings}
          onSelectBuilding={fetchRooms}
          activeBuilding={selectedBuilding}
          onAddBuilding={() => setShowAddModal(true)}
        />

        {/* ADD BUILDING MODAL*/}
        {showAddModal && (
          <div className="modal-overlay">
            <div className="modal">
              <h2>Add Building</h2>
              <input type="text" placeholder="Building Name" value={newBuilding.name} onChange={(e) => setNewBuilding({ ...newBuilding, name: e.target.value })} />
              <input type="text" placeholder="Description" value={newBuilding.description} onChange={(e) => setNewBuilding({ ...newBuilding, description: e.target.value })} />
              <button onClick={addBuilding}>Add Building</button>
              <button onClick={() => setShowAddModal(false)}>Cancel</button>
            </div>
          </div>
        )}

        {showAddRoomModal && (
          <div className="modal-overlay">
            <div className="modal">
              <h2>Add Room to {selectedBuilding.name}</h2>
              <input
                type="text"
                placeholder="Room Name"
                value={newRoomName}
                onChange={(e) => setNewRoomName(e.target.value)}
              />
              <button onClick={() => setShowAddRoomModal(true)}>Add Room</button>
              <button onClick={() => setShowAddRoomModal(false)}>Cancel</button>
            </div>
          </div>
        )}

        {selectedBuilding && (
          <section className="rooms">
            <div className="rooms-header">
              <h2>Rooms in {selectedBuilding}</h2>             
                {/* add rooms function */}
                <div className="controls-right">
                  <button onClick={setShowAddModal}>Add Room</button>
                  <button>Edit Room</button>
              </div>
            </div>

            <div className="rooms-grid">
              {rooms.map((room) => (
                <div
                  key={room.id}
                  className="room-card"
                  onClick={() => fetchDevices(room)}
                >
                  {room.name}
                </div>
              ))}
            </div>
          </section>
        )}

        {/* Modal for Devices */}
        {showModal && selectedRoom && (
          <div className="modal-overlay">
            <div className="modal">
              <button className="modal-close" onClick={closeModal}>Ã—</button>
              <h2>Devices in {selectedRoom.name}</h2>
              <ul style={{ maxHeight: '200px', overflowY: 'auto' }}>
                {devices.length > 0 ? (
                  devices.map((device, i) => <li key={i}>{device.name}</li>)
                ) : (
                  <li>No devices available</li>
                )}
              </ul>
              <div className="modal-buttons">
                <button className="cancel-btn" onClick={closeModal}>Close</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default BuildingNavigation;
